// Leemos la salida del agente final en nuestra cadena: 'Agente 3 - Supervisor'.
const llmOutput = $('Agente 3 - Supervisor').first().json.output;

// Los datos originales ahora vienen del constructor del Agente 3.
const entradaOriginal = $('Constructor de Prompt (Agente 3 - Supervisor)').first().json; 
const { estructuraMEI } = entradaOriginal;

let parsedJson;

// --- INICIO DE LA CORRECCIÓN CLAVE ---
// Usamos una expresión regular para encontrar el primer bloque JSON válido en el texto.
// Esto maneja el caso en que el LLM añade texto antes o después del JSON.
const jsonRegex = /\[[\s\S]*\]/; // Busca un array que empieza con [ y termina con ]
const jsonMatch = llmOutput.match(jsonRegex);

if (jsonMatch && jsonMatch[0]) {
  try {
    // Intentamos parsear solo la parte que coincide con el JSON.
    parsedJson = JSON.parse(jsonMatch[0]);
  } catch (e) {
    parsedJson = null; // Si el bloque extraído sigue siendo inválido, asignamos null.
  }
} else {
  parsedJson = null; // Si no se encuentra ningún bloque JSON, asignamos null.
}
// --- FIN DE LA CORRECCIÓN CLAVE ---


if (!parsedJson || !Array.isArray(parsedJson) || parsedJson.length === 0) {
    return []; // Falla si no se pudo parsear, no es un array o está vacío.
}

let requiredKeys = [];
if (estructuraMEI === 'MEI-Actualizado') {
  requiredKeys = ['indicador', 'justificacionPedagogica'];
} else if (estructuraMEI === 'MEI-Antiguo') {
  requiredKeys = ['habilidad', 'contenido', 'condicion_contexto', 'justificacionPedagogica'];
}

const isStructureValid = parsedJson.every(item => 
    typeof item === 'object' && 
    item !== null && 
    requiredKeys.every(key => Object.prototype.hasOwnProperty.call(item, key))
);

if (isStructureValid) {
  // ÉXITO: Devolvemos el resultado para el nodo de formateo final.
  return [{ 
      json: {
          objetivo_original: entradaOriginal.objetivo_original,
          indicadoresGenerados: parsedJson,
          error: null
      }
  }];
}

// Falla si la estructura de los objetos es incorrecta.
return [];

const payload = $input.first().json.body;
const { estructuraMEI } = payload;
const items = [];

let objetivosADesglosar = [];
let contextoNivelSuperior = {};

if (estructuraMEI === 'MEI-Antiguo') {
  objetivosADesglosar = payload.aprendizajesEsperados;
  contextoNivelSuperior = { resultadosAprendizaje: payload.resultadosAprendizaje };
} else if (estructuraMEI === 'MEI-Actualizado') {
  objetivosADesglosar = payload.resultadosAprendizaje;
  contextoNivelSuperior = { resultadosFormativos: payload.resultadosFormativos };
} else {
  throw new Error(`Estructura MEI no soportada: ${estructuraMEI}`);
}

for (const objetivo of objetivosADesglosar) {
  items.push({
    json: {
      objetivo_actual: objetivo,
      contexto_superior: contextoNivelSuperior,
      estructuraMEI: estructuraMEI
    }
  });
}

return items;

const aggregatedResults = $input.first().json.data;
const originalPayload = $('Webhook').first().json.body;
const { estructuraMEI } = originalPayload;

let finalOutput = { estructuraMEI };

if (estructuraMEI === 'MEI-Antiguo') {
  finalOutput.resultadosAprendizaje = originalPayload.resultadosAprendizaje;
  finalOutput.analisisAprendizajesEsperados = [];
} else if (estructuraMEI === 'MEI-Actualizado') {
  finalOutput.resultadosFormativos = originalPayload.resultadosFormativos;
  finalOutput.analisisResultadosAprendizaje = [];
}

// Iteramos sobre los resultados agregados, que contienen los datos de cada loop.
for (const result of aggregatedResults) {
  // Manejo de errores: Si un loop falló, su 'error' no será null.
  if (result.error) {
      if (!finalOutput.errors) finalOutput.errors = [];
      finalOutput.errors.push({
          objetivoFallido: result.objetivo_original, // objetivo_original SÍ está disponible aquí
          detalleError: result.error
      });
      continue; // Salta al siguiente resultado
  }

  // --- CORRECCIÓN CLAVE ---
  // Construimos el objeto final fusionando el 'objetivo_original' (que contiene id, texto, etc.)
  // con la nueva propiedad 'indicadoresGenerados'.
  const resultadoConIndicadores = {
    ...result.objetivo_original, 
    indicadoresGenerados: result.indicadoresGenerados 
  };
  // -------------------------

  if (estructuraMEI === 'MEI-Antiguo') {
    finalOutput.analisisAprendizajesEsperados.push(resultadoConIndicadores);
  } else if (estructuraMEI === 'MEI-Actualizado') {
    finalOutput.analisisResultadosAprendizaje.push(resultadoConIndicadores);
  }
}

return finalOutput;

// Este nodo SÓLO recibe la salida del RAG de Dominio.
const ragItems = $input.all();

let contexto = "### REGLAS Y DEFINICIONES DEL GLOSARIO UNAB:\n";

for (const item of ragItems) {
  // El input SÍ tiene la estructura item.json.document...
  if (item.json.document && item.json.document.pageContent) {
    contexto += `- ${item.json.document.pageContent}\n`;
  }
}

// Devuelve un único objeto con el texto consolidado.
// El siguiente nodo (Constructor de Prompt) se encargará de fusionar esto
// con los datos del loop.
return [{
    json: {
        contexto_consolidado: contexto
    }
}];

const itemData = $('Procesar Uno por Uno').first().json;
const { objetivo_actual } = itemData;

const promptBase = `Eres un experto en evaluación de competencias profesionales del modelo UNAB. Tu tarea es deconstruir una habilidad compleja en 2 o 3 procesos clave o hitos de desempeño observables.`;

const tareaTexto = `Para el siguiente objetivo, que describe una competencia profesional, identifica los 2 o 3 **procesos clave** que un profesional debe ejecutar para demostrar maestría. Para cada proceso, especifica el verbo de acción observable más importante.

### FILOSOFÍA DE DECONSTRUCCIÓN:
Piensa en las grandes fases o entregables del trabajo de un experto. No en un andamiaje para novatos.
- **ENFOQUE:** Deconstrucción del proceso experto (ej. Analizar el problema -> Diseñar la solución -> Justificar el diseño).

### REGLA DE VERBOS OBSERVABLES:
Están prohibidos los verbos de procesos internos: **conocer, comprender, analizar, reflexionar, valorar**. Usa alternativas que describan la evidencia de esos procesos.

### OBJETIVO A DESGLOSAR:
"${objetivo_actual.texto}"

### FORMATO DE SALIDA OBLIGATORIO:
Tu respuesta DEBE SER ÚNICAMENTE un array JSON de objetos, donde cada objeto tiene dos claves: "paso" (la descripción del proceso) y "verbo_principal" (el verbo observable).
Ejemplo de salida:
[
  { "paso": "Comparar el desempeño y necesidades de los estudiantes utilizando registros de aula", "verbo_principal": "Comparar" },
  { "paso": "Elaborar una propuesta de unidad didáctica ajustada a la diversidad del grupo", "verbo_principal": "Elaborar" },
  { "paso": "Argumentar la selección de estrategias en la unidad, demostrando su impacto", "verbo_principal": "Argumentar" }
]
`;

const promptFinal = `${promptBase}\n\n${tareaTexto}`;

return [{ json: { prompt: promptFinal, ...itemData } }];

const llmOutput = $input.first().json.output;
// Heredamos los datos originales del nodo anterior (Constructor del Agente 1)
const originalData = $('Constructor de Prompt (Agente 1 - Estratega)').first().json;

try {
  const cleanedOutput = llmOutput.replace(/```json|```/g, '').trim();
  const parsedJson = JSON.parse(cleanedOutput);

  // --- CORRECCIÓN CLAVE ---
  // Validamos que sea un array de objetos, y que cada objeto tenga las claves "paso" y "verbo_principal".
  if (
    Array.isArray(parsedJson) &&
    parsedJson.length > 0 &&
    parsedJson.every(item =>
      typeof item === 'object' &&
      item !== null &&
      Object.prototype.hasOwnProperty.call(item, 'paso') &&
      Object.prototype.hasOwnProperty.call(item, 'verbo_principal')
    )
  ) {

    // ÉXITO: Devolvemos un objeto que contiene los datos originales
    // y la nueva progresión pedagógica validada.
    return [{ 
      json: {
        progresionPedagogica: parsedJson,
        ...originalData // Re-inyectamos los datos del item actual del loop
      }
    }];
  }
  // -------------------------

} catch (error) {
  // Si JSON.parse falla, el catch se activa y la función continúa hasta el final.
}

// Si cualquier validación falla, o si hubo un error de parseo,
// retornamos un array vacío para detener esta iteración del loop.
return [];

// Obtenemos la salida del Agente 2 (Redactor Bruto).
const draftOutput = $input.first().json.output;
// Heredamos los datos originales del nodo anterior.
const originalData = $('Constructor de Prompt (Agente 2 - Redactor Bruto)').first().json;
const contextoGlosario = $('Consolidar Contexto').first().json.contexto_consolidado;
const { estructuraMEI, objetivo_actual } = originalData;

let promptBase = `Eres un experto en la filosofía pedagógica del modelo UNAB, entrenado por Andrea. Tu misión es transformar borradores de indicadores en evidencias de aprendizaje impecables y explicar su validez de forma magistral.`;
let tareaTexto = `Toma las siguientes ideas de indicadores y elévalas al estándar de calidad UNAB. Reescríbelas si es necesario para que sean perfectas. Luego, redacta una justificación experta para cada una.`;
let finalInstructions = "";

if (estructuraMEI === 'MEI-Antiguo') {
  finalInstructions = `
### REGLAS FILOSÓFICAS Y DE FORMATO (MANDATO ABSOLUTO):
1.  **PRINCIPIO DE EVIDENCIA PURA (Cero Finalidad):** Un indicador describe una **evidencia observable en presente**, NO un propósito futuro. La finalidad ya está en el AE. Por lo tanto, está **TERMINANTEMENTE PROHIBIDO** usar "para", "con el fin de", "con el propósito de" o cualquier sinónimo. Tu misión es transformar cualquier atisbo de finalidad en una **condición de ejecución** o un **criterio de calidad** observable.
2.  **PRINCIPIO DE ACCIÓN ÚNICA:** El campo 'habilidad' debe contener **UN ÚNICO verbo** en tercera persona plural.
3.  **MISIÓN DE JUSTIFICACIÓN GUÍA:** La 'justificacionPedagogica' debe ser una guía para el equipo de calidad. Debe explicar por qué el indicador es la **evidencia correcta para medir el AE**, conectando explícitamente la habilidad, el contenido y la condición del indicador con las demandas del objetivo superior.

### FORMATO DE SALIDA (JSON Únicamente):
Array de objetos con: "habilidad", "contenido", "condicion_contexto", "justificacionPedagogica".

### EJEMPLO DE ORO (MODELO A SEGUIR):
- **OBJETIVO (AE):** "Diseñar unidades didácticas..."
- **SALIDA ESPERADA:**
  {
    "habilidad": "Comparan",
    "contenido": "enfoques teóricos y necesidades educativas",
    "condicion_contexto": "a partir de registros de aula.",
    "justificacionPedagogica": "Para 'Diseñar' (el AE), es crucial primero poder analizar el contexto. Este indicador mide esa habilidad previa a través de la 'Comparación', que es una acción de análisis observable. El contenido y la condición especifican qué se compara y con qué evidencia, haciendo la medición precisa y relevante para el diseño final."
  }
`;

} else if (estructuraMEI === 'MEI-Actualizado') {
  finalInstructions = `
### REGLAS FILOSÓFICAS Y DE FORMATO (MANDATO ABSOLUTO):
1.  **PRINCIPIO DE EVIDENCIA PURA (Cero Finalidad):** Un indicador describe una **evidencia observable**, NO un propósito futuro. La finalidad ya está en el RA. Por lo tanto, está **TERMINANTEMENTE PROHIBIDO** usar "para" o sinónimos. Tu misión es transformar cualquier finalidad en una **condición de ejecución** o un **criterio de calidad observable**.
2.  **PRINCIPIO DE ACCIÓN ÚNICA:** El indicador debe comenzar con **UN ÚNICO verbo** observable en infinitivo.
3.  **MISIÓN DE JUSTIFICACIÓN GUÍA:** La 'justificacionPedagogica' debe ser una guía para el equipo de calidad. Debe explicar por qué el indicador es la **evidencia correcta para medir el RA**, conectando la acción del indicador (Verbo + Producto/Proceso) con las demandas de la competencia descrita en el objetivo superior.

### FORMATO DE SALIDA (JSON Únicamente):
Array de objetos con: "indicador", "justificacionPedagogica".

### EJEMPLO DE ORO (MODELO A SEGUIR):
- **OBJETIVO (RA):** "Diseñar unidades didácticas..."
- **SALIDA ESPERADA:**
  {
    "indicador": "Comparar el desempeño de los estudiantes utilizando registros de aula.",
    "justificacionPedagogica": "Para medir la competencia de 'Diseñar' (el RA), el profesional primero debe demostrar que puede analizar el contexto. Este indicador mide esa habilidad a través del proceso de 'Comparar desempeño', que es una evidencia observable y un prerrequisito indispensable para un diseño pertinente."
  }
`;
}

const datosEntrada = `### OBJETIVO PRINCIPAL DE REFERENCIA:\n"${objetivo_actual.texto}"
### IDEAS DE INDICADORES A PERFECCIONAR Y JUSTIFICAR:\n${draftOutput}`;
const promptFinal = `${promptBase}\n### CONTEXTO PEDAGÓGICO (para consulta de reglas):\n${contextoGlosario}\n### TAREA:\n${tareaTexto}\n${datosEntrada}\n${finalInstructions}`;

return { json: { prompt: promptFinal, objetivo_original: objetivo_actual, estructuraMEI: estructuraMEI } };

// Obtenemos los datos del nodo validador del Agente 1.
const validatedData = $input.first().json;
const contextoGlosario = $('Consolidar Contexto').first().json.contexto_consolidado;
const { estructuraMEI, objetivo_actual, contexto_superior, progresionPedagogica } = validatedData;

let promptBase = `Eres un redactor de diseño instruccional. Tu función es tomar una estrategia pedagógica y convertirla en un borrador de indicadores formales, enfocándote en la estructura correcta para cada modelo MEI.`;
let contextoTexto = "";
let tareaTexto = "";
let finalInstructions = "";

if (estructuraMEI === 'MEI-Antiguo') {
  const ras = contexto_superior.resultadosAprendizaje.map(ra => `- ${ra.texto}`).join('\\n');
  contextoTexto = `### OBJETIVOS DE NIVEL SUPERIOR (RA):\n${ras}`;
  tareaTexto = `Para cada paso, redacta un borrador de Indicador de Logro (IL) descompuesto, enfocándote en el **Contenido** temático.`;

  finalInstructions = `
### INSTRUCCIONES DE REDACCIÓN (MEI ANTIGUO):
1.  **HABILIDAD:** Usa el 'verbo_principal' proporcionado, conjugado en tercera persona plural.
2.  **ESTRUCTURA:** Rellena los campos 'habilidad', 'contenido' (el conocimiento o tema), y 'condicion_contexto'.
3.  **FORMATO DE SALIDA:** Responde ÚNICAMENTE con un array JSON de objetos con los campos: habilidad, contenido, condicion_contexto.
`;

} else if (estructuraMEI === 'MEI-Actualizado') {
  const rfs = contexto_superior.resultadosFormativos.map(rf => `- ${rf.texto}`).join('\\n');
  contextoTexto = `### OBJETIVO DE NIVEL SUPERIOR (RF):\n${rfs}`;
  tareaTexto = `Para cada paso, redacta un borrador de Indicador de Desempeño (ID) como una frase completa, enfocándote en un **Producto o Proceso** observable.`;

  finalInstructions = `
### INSTRUCCIONES DE REDACCIÓN (MEI ACTUALIZADO):
1.  **VERBO:** La frase debe comenzar con el 'verbo_principal' proporcionado, en infinitivo.
2.  **ESTRUCTURA:** La frase debe seguir el formato: Verbo + **Producto** (tangible) o **Proceso** (observable) + Condición/Contexto.
3.  **FORMATO DE SALIDA:** Responde ÚNICAMENTE con un array JSON de strings, donde cada string es el indicador redactado.
`;
}

const datosEntrada = `### OBJETIVO PRINCIPAL A DESGLOSAR:\n"${objetivo_actual.texto}"
### PROGRESIÓN PEDAGÓGICA A CONVERTIR:\n${JSON.stringify(progresionPedagogica, null, 2)}`;
const promptFinal = `${promptBase}\n${contextoTexto}\n### CONTEXTO PEDAGÓGICO:\n${contextoGlosario}\n### TAREA:\n${tareaTexto}\n${datosEntrada}\n${finalInstructions}`;

return {
    json: {
        prompt: promptFinal,
        objetivo_actual: objetivo_actual,
        contexto_superior: contexto_superior,
        estructuraMEI: estructuraMEI,
    }
};
