const items = $input.all();
let styleGuide = "### GUÍA DE ESTILO DEL VOCABULARIO (Schema):\n";

// Iteramos sobre cada ítem que nos llega
for (const item of items) {
  // Verificamos que la estructura anidada exista
  if (item.json && item.json.document && item.json.document.metadata) {

    // Usamos los paths correctos: item.json.document.pageContent
    const clave = item.json.document.pageContent;

    // Y item.json.document.metadata.descripcion
    const descripcion = item.json.document.metadata.descripcion || "Sin descripción";

    styleGuide += `- Usa la clave: \`${clave}\` (Descripción: ${descripcion})\n`;
  }
}

// Devolvemos el texto completo
return { json: { style_guide_text: styleGuide } };

