// Obtiene la salida de texto crudo del nodo anterior
const rawOutput = $input.item.json.output;

// Expresión regular para encontrar la primera ocurrencia de un bloque JSON
const jsonRegex = /\{[\s\S]*\}/;
const match = rawOutput.match(jsonRegex);

// Si se encuentra una coincidencia, la parseamos a un objeto JSON
if (match && match[0]) {
  const jsonObject = JSON.parse(match[0]);
  // Devuelve el objeto JSON limpio. N8N lo pondrá dentro de un objeto 'json'.
  return { json: jsonObject };
}

// Si no se encuentra ningún JSON, devuelve un error para detener el flujo
return { json: { error: "No se encontró un objeto JSON válido en la salida del LLM." } };

// Recibe el array completo desde el nodo Supabase Vector Store
const items = $input.all();

// Prepara un nuevo array para guardar los resultados limpios
const cleanedContext = [];

// Itera sobre cada documento recuperado
for (const item of items) {
  // Verifica que la estructura esperada exista para evitar errores
  if (item.json && item.json.document && item.json.document.metadata) {

    const doc = item.json.document;

    // Construye un nuevo objeto solo con los campos que necesitamos
    const cleanItem = {
      // Mantenemos el 'pageContent' que es el texto principal
      content: doc.pageContent,

      // Creamos un nuevo objeto 'metadata' solo con los campos útiles
      metadata: {
        termino: doc.metadata.termino,
        fuente: doc.metadata.fuente,
        paradigma: doc.metadata.paradigma,
        tipo: doc.metadata.tipo,
        nivelTaxonomico: doc.metadata.nivelTaxonomico,
        definicionGeneralNivel: doc.metadata.definicionGeneralNivel
      }
    };

    // Agrega el objeto limpio a nuestro array de resultados
    cleanedContext.push(cleanItem);
  }
}

// Devuelve el array de objetos limpios.
// El siguiente nodo recibirá esta data simplificada.
return cleanedContext;
