// Obtiene los datos binarios del archivo de texto
const binaryData = $input.item.binary.data;
// Convierte el buffer a un string UTF-8
const text = Buffer.from(binaryData.data, 'base64').toString('utf-8');
// Retorna el texto en la estructura que n8n espera
return { json: { textoExtraido: text } };

// Recibe el output de Gemini como un string JSON
let geminiOutputString = $input.first().json.text;

// PASO DE LIMPIEZA
const cleanedString = geminiOutputString.replace(/^```json\n/, '').replace(/\n```$/, '');

let diGenerado;
try {
  diGenerado = JSON.parse(cleanedString);
} catch (error) {
  throw new Error("El output de Gemini no es un JSON válido. Output: " + cleanedString);
}

// --- NUEVO PASO DE VERIFICACIÓN PARA ENCONTRAR EL ARRAY --
// Verificamos si el output de la IA es un objeto con la clave '@graph'
// o si es directamente el array que necesitamos.
let graphArray;
if (diGenerado && Array.isArray(diGenerado["@graph"])) {
  // Caso 1: El formato es correcto, con {"@graph": [...]}
  graphArray = diGenerado["@graph"];
} else if (Array.isArray(diGenerado)) {
  // Caso 2: La IA devolvió directamente el array [...]
  graphArray = diGenerado;
  // Reconstruimos el objeto principal para que tenga la estructura correcta
  diGenerado = { "@graph": graphArray };
} else {
  // Si no es ninguna de las dos, no podemos continuar.
  throw new Error("El JSON generado por la IA no contiene un array en la clave '@graph' ni es un array en sí mismo.");
}

// 1. REEMPLAZAR EL @CONTEXT POR EL CORRECTO Y DEFINITIVO
const contextoCorrecto = {
    "@vocab": "http://example.org/atdf102#",
    "xsd": "http://www.w3.org/2001/XMLSchema#",
    "rdfs": "http://www.w3.org/2000/01/rdf-schema#",
    "schema": "http://schema.org/",
    "qm": "http://example.org/qm-standards/",
    "Curso": "atdf:Curso", "UnidadDidactica": "atdf:UnidadDidactica", "AprendizajeEsperado": "atdf:AprendizajeEsperado",
    "IndicadorLogro": "atdf:IndicadorLogro", "ActividadAprendizaje": "atdf:ActividadAprendizaje", "Evaluacion": "atdf:Evaluacion",
    "InstrumentoEvaluacion": "atdf:InstrumentoEvaluacion", "RecursoAprendizaje": "atdf:RecursoAprendizaje", "Semana": "atdf:Semana",
    "PoliticaCurso": "atdf:PoliticaCurso", "ResultadoAprendizajeGeneral": "atdf:ResultadoAprendizajeGeneral",
    "codigo": "schema:identifier", "nombre": "schema:name", "descripcion": "schema:description",
    "creditos": "atdf:creditos", "numeroSemanas": { "@id": "atdf:numeroSemanas", "@type": "xsd:integer" },
    "fechaInicio": { "@id": "atdf:fechaInicio", "@type": "xsd:date" }, "modalidad": "atdf:modalidad",
    "docenteExperto": "atdf:docenteExperto", "disenadorInstruccional": "atdf:disenadorInstruccional", "gpo": "atdf:gpo",
    "horasDirectasTotal": { "@id": "atdf:horasDirectasTotal", "@type": "xsd:integer" }, "verboAccion": "atdf:verboAccion",
    "nivelTaxonomico": "atdf:nivelTaxonomico", "tipoEvaluacion": { "@id": "atdf:tipoEvaluacion", "@type": "xsd:string" },
    "metodoEvaluacion": "atdf:metodoEvaluacion", "procedimientoEvaluacion": "atdf:procedimientoEvaluacion",
    "pesoCalificacion": { "@id": "atdf:pesoCalificacion", "@type": "xsd:decimal" }, "retroalimentacion": "atdf:retroalimentacion",
    "estrategiaDidactica": { "@id": "atdf:estrategiaDidactica", "@container": "@list" }, "formatoRecurso": "atdf:formatoRecurso",
    "urlRecurso": { "@id": "schema:url", "@type": "@id" }, "fuente": "schema:citation",
    "numeroSemanaOrdinal": { "@id": "atdf:numeroSemanaOrdinal", "@type": "xsd:integer" },
    "tieneUnidad": { "@id": "atdf:tieneUnidad", "@type": "@id", "@container": "@set" },
    "tieneAE": { "@id": "atdf:tieneAE", "@type": "@id", "@container": "@set" },
    "tieneIndicador": { "@id": "atdf:tieneIndicador", "@type": "@id", "@container": "@set" },
    "contieneSemana": { "@id": "atdf:contieneSemana", "@type": "@id", "@container": "@set" },
    "contieneActividad": { "@id": "atdf:contieneActividad", "@type": "@id", "@container": "@set" },
    "contieneEvaluacion": { "@id": "atdf:contieneEvaluacion", "@type": "@id", "@container": "@set" },
    "ocurreEn": { "@id": "atdf:ocurreEn", "@type": "@id" },
    "evaluaIndicador": { "@id": "atdf:evaluaIndicador", "@type": "@id", "@container": "@set" },
    "apoyaIndicador": { "@id": "atdf:apoyaIndicador", "@type": "@id", "@container": "@set" },
    "utilizaInstrumento": { "@id": "atdf:utilizaInstrumento", "@type": "@id" },
    "utilizaRecurso": { "@id": "atdf:utilizaRecurso", "@type": "@id", "@container": "@set" },
    "precede": { "@id": "atdf:precede", "@type": "@id" },
    "cumpleEstandarQA": { "@id": "qm:relatesToStandard", "@type": "@id", "@container": "@set" },
    "tienePolitica": { "@id": "atdf:tienePolitica", "@type": "@id", "@container": "@set" },
    "tieneResultadoGeneral": {"@id": "atdf:tieneResultadoGeneral", "@type": "@id"}
};
diGenerado["@context"] = contextoCorrecto;

// 2. CORREGIR EL FORMATO DE LOS ENLACES
const propiedadesDeEnlace = [
  'tieneUnidad', 'tieneAE', 'tieneIndicador', 'contieneSemana',  
  'contieneActividad', 'contieneEvaluacion', 'ocurreEn',  
  'evaluaIndicador', 'apoyaIndicador', 'utilizaInstrumento',  
  'utilizaRecurso', 'unidadesDidacticas', 'semanas',  
  'actividadesAprendizaje', 'evaluaciones', 'indicadoresLogro',  
  'instrumentoEvaluacionRef'
];

// Ahora iteramos sobre la variable segura 'graphArray'
for (const entidad of graphArray) {
  for (const propiedad of propiedadesDeEnlace) {
    if (entidad[propiedad]) {
      const valor = entidad[propiedad];
      if (Array.isArray(valor)) {
        entidad[propiedad] = valor.map(id => (typeof id === 'string' ? { "@id": id } : id));
      } else if (typeof valor === 'string') {
        entidad[propiedad] = { "@id": valor };
      }
    }
  }
}

// Aseguramos que el objeto principal tenga el array procesado antes de retornar
diGenerado["@graph"] = graphArray;

return diGenerado;
