// Obtenemos los datos de la iteración actual referenciando el nodo Split In Batches. ESTA ES LA LÓGICA CORRECTA.
const itemData = $('Procesar Uno por Uno').first().json;

// Obtenemos el contexto del nodo inmediatamente anterior (Consolidar Contexto) usando $input.
const contextoGlosario = $input.first().json.contexto_consolidado;

// Ahora la desestructuración funcionará porque 'itemData' tiene los datos correctos.
const { estructuraMEI, objetivo_actual, contexto_superior } = itemData;

let promptBase = `Eres un experto diseñador instruccional de la UNAB. Tu rol es seguir las reglas pedagógicas de forma estricta y precisa, y justificar tus decisiones con claridad.`;
let contextoTexto = "";
let tareaTexto = "";
let tipoIndicador = "";

// La lógica de protección dentro de los if/else sigue siendo una buena práctica.
if (estructuraMEI === 'MEI-Antiguo') {
  tipoIndicador = "Indicadores de Logro (IL)";
  const ras = contexto_superior.resultadosAprendizaje.map(ra => `- ${ra.texto}`).join('\\n');
  contextoTexto = `### OBJETIVOS DE NIVEL SUPERIOR (Resultados de Aprendizaje):\n${ras}`;
  tareaTexto = `Genera una lista de 2 a 4 ${tipoIndicador} que desglosen operativamente el siguiente Aprendizaje Esperado (AE).`;

} else if (estructuraMEI === 'MEI-Actualizado') {
  tipoIndicador = "Indicadores de Desempeño (ID)";
  const rfs = contexto_superior.resultadosFormativos.map(rf => `- ${rf.texto}`).join('\\n');
  // --- LÍNEA CORREGIDA ---
  // Se reemplaza la variable incorrecta 'rf_text' por la variable procesada 'rfs'.
  contextoTexto = `### OBJETIVO DE NIVEL SUPERIOR (Resultado Formativo):\n${rfs}`;
  // -----------------------
  tareaTexto = `Genera una lista de 3 a 5 ${tipoIndicador} que desglosen operativamente el siguiente Resultado de Aprendizaje (RA).`;
}

let datosEntrada = `### ${estructuraMEI === 'MEI-Antiguo' ? 'APRENDIZAJE ESPERADO' : 'RESULTADO DE APRENDIZAJE'} A DESGLOSAR:\n"${objetivo_actual.texto}"`;
if (objetivo_actual.contenido) {
  datosEntrada += `\n\n### CONTENIDO ASOCIADO:\n${objetivo_actual.contenido}`;
}
if (objetivo_actual.metodologia) {
  datosEntrada += `\n\n### METODOLOGÍA PROPUESTA:\n${objetivo_actual.metodologia}`;
}

const finalInstructions = `
### INSTRUCCIONES DE GENERACIÓN Y FORMATO (MUY IMPORTANTE):
Tu tarea es generar un array JSON de objetos. Sigue estas reglas de forma estricta:

1.  **REGLA TAXONÓMICA INFLEXIBLE:** El verbo del indicador que generes DEBE ser de un nivel taxonómico **IGUAL O INFERIOR** al del verbo del objetivo a desglosar. **NUNCA SUPERIOR.**

2.  **REGLA DE SINTAXIS:** El indicador describe una **evidencia observable**, no un propósito. Por lo tanto, NO DEBE usar la palabra "para" para expresar finalidad.

3.  **FORMATO DE SALIDA:** Tu respuesta DEBE SER ÚNICAMENTE un array JSON de objetos. Para cada objeto, rellena los siguientes campos:
    *   **indicador**: El texto completo del indicador.
    *   **verbo**: El verbo principal en infinitivo.
    *   **justificacionSemantica**: Explica por qué el indicador es una evidencia relevante del objetivo.
    *   **justificacionTaxonomica**: En una sola frase, identifica el verbo y nivel del objetivo, el verbo y nivel del indicador, declara la relación ('inferior' o 'igual'), y explica brevemente por qué es coherente.
    *   **justificacionSintactica**: Explica por qué la sintaxis del indicador es correcta, enfocándose en cómo describe una **evidencia observable** que **contribuye a la finalidad ya expresada en el objetivo padre**, en lugar de definir una nueva finalidad.

### EJEMPLOS DE APLICACIÓN DE LAS REGLAS DE JUSTIFICACIÓN:

**EJEMPLO 1:**
- **Objetivo a desglosar:** "Analizar el desempeño del proyecto..." (Finalidad: analizar desempeño)
- **Salida del indicador:**
  {
    "indicador": "Calcular los índices de desempeño (CPI y SPI) para determinar la salud del proyecto.",
    "verbo": "Calcular",
    "justificacionSemantica": "El indicador desglosa la tarea de 'analizar el desempeño' en la acción medible de calcular los índices CPI y SPI, que son métricas fundamentales para dicho análisis.",
    "justificacionTaxonomica": "El verbo del objetivo es 'Analizar' (Nivel 4) y el del indicador es 'Calcular' (Nivel 3), siendo la relación 'inferior', lo cual es coherente ya que el cálculo es un prerrequisito para el análisis.",
    "justificacionSintactica": "La sintaxis es correcta porque describe la evidencia observable (el cálculo de los índices) que permite cumplir la finalidad de 'analizar el desempeño' ya definida en el objetivo, sin introducir un nuevo propósito."
  }

**EJEMPLO 2 (INCORRECTO Y PROHIBIDO - VIOLACIÓN TAXONÓMICA):**
- **Objetivo a desglosar:** "Implementar una campaña de email marketing..." (Verbo 'Implementar', Nivel 3)
- **Salida inválida (verbo superior):**
  {
    "indicador": "Diseñar el flujo de automatización de la campaña...",
    "verbo": "Diseñar" // Verbo 'Diseñar' (Nivel 6) es SUPERIOR a 'Implementar' (Nivel 3). ESTO ESTÁ PROHIBIDO.
  }
`;

const promptFinal = `
${promptBase}

### CONTEXTO PEDAGÓGICO (REGLAS OBLIGATORIAS):
${contextoGlosario}

${contextoTexto}

### TAREA ESPECÍFICA:
${tareaTexto}

${datosEntrada}

${finalInstructions}
`;

return { json: { prompt: promptFinal, objetivo_original: objetivo_actual } };

// --- CONFIGURACIÓN DE LA PRUEBA ---
// Este payload está diseñado para probar la nueva regla taxonómica inflexible.
// El AE-2 ("Relacionar...") es la trampa principal, ya que una forma natural de
// evidenciar esa relación es a través de una "Argumentación" (nivel superior).
// El objetivo es ver si el sistema se restringe a sí mismo y genera indicadores
// de nivel 4 o inferior, cumpliendo la nueva orden.
const testCase = 'MEI-Antiguo-InflexibleRuleTest';
// ---------------------------------

let payload = {};

if (testCase === 'MEI-Antiguo-InflexibleRuleTest') {
  payload = {
    "estructuraMEI": "MEI-Antiguo",
    "resultadosAprendizaje": [
      {
        "id": "RA-1",
        "texto": "Analizar textos narrativos complejos, aplicando marcos teóricos de la crítica literaria para interpretar su estructura, estilo y significado."
      }
    ],
    "aprendizajesEsperados": [
      {
        "id": "AE-1",
        "tributaA": ["RA-1"],
        "texto": "Analizar la función del narrador en la construcción del punto de vista de una novela."
      },
      {
        "id": "AE-2", // LA TRAMPA PRINCIPAL
        "tributaA": ["RA-1"],
        "texto": "Relacionar un texto literario con su contexto histórico y cultural para identificar posibles influencias y diálogos intertextuales."
      },
      {
        "id": "AE-3",
        "tributaA": ["RA-1"],
        "texto": "Aplicar un marco teórico específico (ej. estructuralismo) para interpretar la estructura de un cuento."
      }
    ]
  };
} else {
  // Mantener otros casos para facilidad de prueba
  payload = { "estructuraMEI": "Otro" };
}

return { json: { body: payload } };

