const rawOutput = $json.output;
const jsonRegex = /\[[\s\S]*\]/; // Busca un array JSON
const match = rawOutput.match(jsonRegex);

if (match && match[0]) {
  const jsonArray = JSON.parse(match[0]);
  // Devuelve el array parseado
  return { json: { glosarioParseado: jsonArray } };
}
return { json: { error: "No se encontró un array JSON válido en la salida del LLM." } };
