const rawOutput = $input.item.json.output;
const jsonRegex = /\{[\s\S]*\}/;
const match = rawOutput.match(jsonRegex);

if (!match || !match[0]) {
  throw new Error("Agente Extractor de AEs no generó un JSON válido.");
}

const data = JSON.parse(match[0]);

if (!data.aprendizajesEsperados || !Array.isArray(data.aprendizajesEsperados)) {
  throw new Error("El JSON de AEs no contiene un array 'aprendizajesEsperados' válido.");
}

return { json: data };

// El nodo Merge nos entrega un array con los resultados de las dos ramas.
const inputs = $input.all();

// Extraemos los datos de cada rama
const dataRAs = inputs.find(item => item.json.resultadosAprendizaje).json;
const dataAEs = inputs.find(item => item.json.aprendizajesEsperados).json;

if (!dataRAs || !dataAEs) {
  throw new Error("Faltan datos de una de las ramas de extracción para ensamblar el JSON final.");
}

// Creamos el objeto JSON final combinando las propiedades de ambos
const finalJSON = {
  ...dataRAs,
  ...dataAEs
};

return { json: finalJSON };
