// --- Inicio: Código CORREGIDO para "Validar y Extraer Indicadores" (ID 2615...) ---

// 1. OBTENER DATOS DE ENTRADA
const itemData = $input.first().json;
const rawResponse = itemData.output;
// Buscamos la estructura MEI del nodo de bucle
const estructuraMEI = $('Procesar Uno por Uno').first().json.estructuraMEI; 

// 2. FUNCIÓN DE EXTRACCIÓN (Simple)
function extractJsonArray(text) {
  const arrayStart = text.indexOf('[');
  const arrayEnd = text.lastIndexOf(']');
  if (arrayStart === -1 || arrayEnd === -1) {
    throw new Error("Respuesta de IA no contiene un array JSON válido.");
  }
  const jsonString = text.substring(arrayStart, arrayEnd + 1);
  return JSON.parse(jsonString);
}

// 3. PARSEAR LA RESPUESTA
const indicadoresGeneradosLLM = extractJsonArray(rawResponse);


// 4. TRANSFORMACIÓN CONDICIONAL
let indicadoresGenerados;

if (estructuraMEI === 'MEI-Actualizado') {
    // Mapeo para la estructura del frontend (RF-RA-ID)
    indicadoresGenerados = indicadoresGeneradosLLM.map(indicador => {
        return {
            // *** CORRECCIÓN CLAVE ***
            // El Agente 3 devuelve "id_texto_original", no "id_texto".
            "id_texto": indicador.id_texto, 

            "verbo_utilizado": indicador.verbo_utilizado,
            "nivel_verbo": indicador.nivel_verbo,
            "justificacion_pedagogica": indicador.justificacion_pedagogica
        };
    });
} else if (estructuraMEI === 'MEI-Antiguo') {
    // Mapeo para la estructura de la tabla del frontend (RA-AE-IL)
    indicadoresGenerados = indicadoresGeneradosLLM.map(indicador => {
        return {
            // Usamos las claves con Mayúscula que el Agente 3 SÍ devuelve
            "habilidad": indicador.Habilidad,
            "contenido": indicador.Contenido,
            "condicion_contexto": indicador.Condicion_Contexto,
            "justificacion_pedagogica": indicador.justificacion_pedagogica
        };
    });
} else {
    indicadoresGenerados = [];
}

// 5. PREPARAR EL ITEM DE SALIDA (FUSIÓN)
const outputItem = {
  objetivo_actual: itemData.objetivo_actual,
  contexto_superior: itemData.contexto_superior,
  estructuraMEI: estructuraMEI, 
  indicadoresGenerados: indicadoresGenerados 
};

// 6. DEVOLVER EL OBJETO LIMPIO
return [outputItem];

// --- Fin: Código CORREGIDO para "Validar y Extraer Indicadores" (ID 2615...) ---

const payload = $input.first().json.body;
const { estructuraMEI } = payload;
const items = [];

let objetivosADesglosar = [];
let contextoNivelSuperior = {};

if (estructuraMEI === 'MEI-Antiguo') {
  objetivosADesglosar = payload.aprendizajesEsperados;
  contextoNivelSuperior = { resultadosAprendizaje: payload.resultadosAprendizaje };
} else if (estructuraMEI === 'MEI-Actualizado') {
  objetivosADesglosar = payload.resultadosAprendizaje;
  contextoNivelSuperior = { resultadosFormativos: payload.resultadosFormativos };
} else {
  throw new Error(`Estructura MEI no soportada: ${estructuraMEI}`);
}

for (const objetivo of objetivosADesglosar) {
  items.push({
    json: {
      objetivo_actual: objetivo,
      contexto_superior: contextoNivelSuperior,
      estructuraMEI: estructuraMEI
    }
  });
}

return items;

const aggregatedResults = $input.first().json.data;
const originalPayload = $('Webhook').first().json.body;
const { estructuraMEI } = originalPayload;

let finalOutput = { estructuraMEI };

if (estructuraMEI === 'MEI-Antiguo') {
  finalOutput.resultadosAprendizaje = originalPayload.resultadosAprendizaje;
  finalOutput.analisisAprendizajesEsperados = [];
} else if (estructuraMEI === 'MEI-Actualizado') {
  finalOutput.resultadosFormativos = originalPayload.resultadosFormativos;
  finalOutput.analisisResultadosAprendizaje = [];
}

// Iteramos sobre los resultados agregados, que contienen los datos de cada loop.
for (const result of aggregatedResults) {
  // Manejo de errores: Si un loop falló, su 'error' no será null.
  if (result.error) {
      if (!finalOutput.errors) finalOutput.errors = [];
      finalOutput.errors.push({
          objetivoFallido: result.objetivo_original, // objetivo_original SÍ está disponible aquí
          detalleError: result.error
      });
      continue; // Salta al siguiente resultado
  }

  // --- CORRECCIÓN CLAVE ---
  // Construimos el objeto final fusionando el 'objetivo_original' (que contiene id, texto, etc.)
  // con la nueva propiedad 'indicadoresGenerados'.
  const resultadoConIndicadores = {
    ...result.objetivo_original, 
    indicadoresGenerados: result.indicadoresGenerados 
  };
  // -------------------------

  if (estructuraMEI === 'MEI-Antiguo') {
    finalOutput.analisisAprendizajesEsperados.push(resultadoConIndicadores);
  } else if (estructuraMEI === 'MEI-Actualizado') {
    finalOutput.analisisResultadosAprendizaje.push(resultadoConIndicadores);
  }
}

return finalOutput;

// --- Inicio: Código CORREGIDO para "Constructor de Prompt (Agente 2 - Potente)" ---

// 1. OBTENER DATOS (Leyendo de Nodos Anteriores)
const estructuraMEI = $('Webhook').first().json.body.estructuraMEI;
const objetivo_actual = $('Procesar Uno por Uno').first().json.objetivo_actual;
const contexto_superior = $('Procesar Uno por Uno').first().json.contexto_superior;

// *** CRÍTICO: El nivel_ra ahora es el NIVEL del objetivo principal (RA o AE) ***
const nivel_ra = $('Verbos y Nivel').first().json.nivel_ra;

const verbos_string = $('Verbos y Nivel').first().json.verbos;
const verbos_objeto = JSON.parse(verbos_string);
const lista_verbos_permitidos = verbos_objeto.lista_verbos_permitidos;

// Datos del Agente 1 (Storytelling)
const itemData = $input.first().json; // Output de "Validar Salida Agente 1"
const { rol_profesional_simulado, escenario_desafio_narrativo } = itemData;

// 2. PREPARAR PROMPT BASE (SYSTEM PROMPT)
const promptBase = `Actúa como especialista en currículo, evaluación y metodologías activas en educación superior, con formación avanzada en diseño instruccional y enfoque por competencias. Eres experto metodológico y didáctico en el curso [${rol_profesional_simulado}].

Tu tarea es redactar [número] indicadores de desempeño (IDs) alineados con el Resultado de Aprendizaje.

### RESTRICCIÓN DE ORO (MANDATO ABSOLUTO):
**CERO TOLERANCIA A LA FINALIDAD**. El indicador es la "evidencia", no el "propósito".
Está **TERMINANTEMENTE PROHIBIDO** usar las palabras "para", "con el fin de", "con el propósito de" o cualquier sinónimo.

### TU RAZONAMIENTO (CÓMO PENSAR):
El aprendizaje es un "Árbol de Dependencias". Tu tarea es **inducir el árbol de dependencias lógicas** (los "peldaños") para construir el objetivo principal.
- Tu meta es la **coherencia pedagógica**, no la simetría taxonómica.

Tu sintaxis sigue la "Regla del Blueprint" (El Gerundio):
- La "Spec" de desempeño se hace observable usando un **sub-proceso (gerundio)**.
- *Ejemplo:* "COMPARAN las teorías (proceso), **diferenciando** (gerundio) sus componentes..."
`;

// 3. PREPARAR TAREA (USER PROMPT)
let tareaTexto = "";
let contextoPrincipal = "";
let finalInstructions = "";

if (estructuraMEI === 'MEI-Antiguo') {
  // --- LÓGICA MEI-ANTIGUO (Aplicamos la lógica de "Relevancia Cognitiva") ---
  const ras = contexto_superior.resultadosAprendizaje.map(ra => `- ${ra.texto}`).join('\n');
  contextoPrincipal = `### OBJETIVOS DE NIVEL SUPERIOR (RA):\n${ras}`;
  tareaTexto = `### OBJETIVO PRINCIPAL A CONSTRUIR (AE):
"${objetivo_actual.texto}"
`;
  finalInstructions = `
### INSTRUCCIONES DE GENERACIÓN (MEI ANTIGUO - INDICADORES DE LOGRO):
1.  **Inducir Árbol:** Induce el "Árbol de Dependencias" (3 peldaños) para el AE. **Guía Afirmativa:** Para un AE de Nivel ${nivel_ra}, tus componentes deben ser **cognitivamente relevantes**.
2.  **Regla de Anclaje:** El *último* indicador debe ser el que cumple el AE.
3.  **Sintaxis (Conjugación):** Conjuga el verbo en **TERCERA PERSONA PRESENTE INDICATIVO PLURAL** (ej. "Comparan").
4.  **Nivel (Fidelidad):** El "nivel_verbo" DEBE ser el número exacto **copiado** de la "LISTA DE MATERIALES".
5.  **Formato de Salida:** Responde ÚNICAMENTE con un array JSON de objetos. Cada objeto debe tener TRES claves:
    - "habilidad": el verbo conjugado correctamente (Ej: "DIFERENCIAN").
    - "contenido": el proceso principal.
    - "condicion_contexto": la condición o sub-proceso (gerundio).
`;

} else if (estructuraMEI === 'MEI-Actualizado') {
  // --- LÓGICA MEI-ACTUALIZADO (SIN CAMBIOS) ---
  const rfs = contexto_superior.resultadosFormativos.map(rf => `- ${rf.texto}`).join('\n');
  contextoPrincipal = `### OBJETIVO DE NIVEL SUPERIOR (RF):\n${rfs}`;
  tareaTexto = `### STORYTELLING (CONTEXTO DEL EDIFICIO):
- **Rol:** ${rol_profesional_simulado}
- **Escenario:** ${escenario_desafio_narrativo}
### PLANO MAESTRO A CONSTRUIR (RA):
"${objetivo_actual.texto}"
`;
  finalInstructions = `
### INSTRUCCIONES DE GENERACIÓN (MEI ACTUALIZADO):
1.  **Inducir Árbol:** Induce el "Árbol de Dependencias" (2-3 peldaños) para el RA. **Guía Afirmativa:** Para un RA de alto nivel (como este, Nivel ${nivel_ra}), tus componentes deben ser **cognitivamente relevantes**.
2.  **Regla de Anclaje:** El *último* indicador debe ser uno que iguale el nivel el RA (en este caso, nivel ${nivel_ra}).
3. Sintaxis (Conjugación): Asegúrate de que "id_texto" comience con un verbo conjugado en TERCERA PERSONA PRESENTE PLURAL INDICATIVO (ej. "ANALIZAN", "EXPLICAN", "INTEGRAN").
4.  **Nivel (Fidelidad):** El "nivel_verbo" DEBE ser el número exacto **copiado** de la "LISTA DE MATERIALES".
5.  **Formato de Salida:** Responde ÚNICAMENTE con un array JSON de objetos. Cada objeto debe tener TRES claves:
    - "id_texto": la frase completa (la "Spec") ensamblada.
    - "verbo_utilizado": el verbo conjugado en TERCERA PERSONA PRESENTE PLURAL INDICATIVO.
    - "nivel_verbo": el número de nivel (ej. 4) copiado de la lista.
    - "justificacion_pedagogica". En ella, explica en 1 a 2 oraciones cómo este indicador se relaciona con el RA y qué tipo de proceso y/o producto mide.
`;
}

// 4. ENSAMBLAR Y DEVOLVER
const promptFinal = `
${promptBase}
${contextoPrincipal}
${tareaTexto}

### LISTA DE MATERIALES (VERBOS PERMITIDOS CON NIVEL):
${JSON.stringify(lista_verbos_permitidos, null, 2)}

${finalInstructions}

### TU TAREA:
Genera el array JSON de indicadores (ILs o IDs) según la estructura MEI, aplicando tu razonamiento.
`;

return [{ 
  json: { 
    prompt: promptFinal, 
    ...itemData // Pasa todos los datos que ya teníamos
  } 
}];

// --- Fin: Código CORREGIDO para "Constructor de Prompt (Agente 2)" ---

// --- Inicio: Código CORREGIDO para "Constructor de Prompt (Agente 3 - Supervisor)" ---

// 1. OBTENER DATOS
// El input SÓLO tiene el borrador del agente anterior
const inputData = $input.first().json;
const { borrador_indicadores } = inputData;

// El contexto (estructura, objetivo) se lee del NODO DE BUCLE 'Procesar Uno por Uno'
const loopData = $('Procesar Uno por Uno').first().json;
const { 
  estructuraMEI, 
  objetivo_actual, 
  contexto_superior 
} = loopData;

// VERIFICACIÓN DE SEGURIDAD
if (!estructuraMEI) {
  throw new Error("Error Crítico: 'estructuraMEI' no se pudo leer desde el nodo 'Procesar Uno por Uno'.");
}
if (!borrador_indicadores) {
  throw new Error("Error Crítico: 'borrador_indicadores' no fue recibido del nodo anterior.");
}


// 2. PREPARAR PROMPT BASE (SYSTEM PROMPT)
const promptBase = `Eres un "Supervisor de Calidad" de UNAB. Tu trabajo es tomar un **borrador** de indicadores y aplicar la **REGLA DE ORO** final.

### RESTRICCIÓN DE ORO (MANDATO ABSOLUTO):
**CERO TOLERANCIA A LA FINALIDAD**. El indicador es la "evidencia", no el "propósito".
Está **TERMINANTEMENTE PROHIBIDO** usar las palabras "para", "con el fin de", "con el propósito de" o cualquier sinónimo.

### RESTRICCIÓN DE ESTRUCTURA (MANDATO CRÍTICO):
DEBES mantener la estricta separación de los tres componentes (Habilidad, Contenido, Condición/Contexto) en claves separadas. NO FUSIONES el contenido y la condición/contexto. Mantén el "TERCERA PERSONA PRESENTE INDICATIVO PLURAL" del verbo inicial de cada indicador.
`;

// 3. PREPARAR TAREA (USER PROMPT)
let finalInstructions = "";

if (estructuraMEI === 'MEI-Antiguo') {
  // --- LÓGICA MEI-ANTIGUO ---
  finalInstructions = `
### INSTRUCCIONES DE CORRECCIÓN (MEI ANTIGUO):
Revisa cada objeto en el "BORRADOR DE INDICADORES".
1.  **Validar Finalidad:** Si encuentras CUALQUIER palabra de finalidad (como "para") en "habilidad", "contenido" o "condicion_contexto", **reescribe** esa clave para eliminarla.
2.  **Validar Sintaxis (Crucial):** Asegúrate de que "habilidad" sea un verbo en **TERCERA PERSONA PRESENTE INDICATIVO PLURAL** (Ej: "INTEGRAN").
3.  **Preservar Datos (MANDATORIO):** Mantén intactas las claves **verbo_utilizado**, **nivel_verbo** y **justificacion_pedagogica** del borrador original.
4.  **Formato de Salida:** Devuelve ÚNICAMENTE el array JSON corregido.

### EJEMPLO DE OBJETO JSON DE SALIDA (MEI ANTIGUO):
[
  {
    "habilidad": "DIFERENCIAN las teorías...",
    "contenido": "El contenido sin finalidad...",
    "condicion_contexto": "El contexto sin finalidad...",
    "verbo_utilizado": "DIFERENCIAR",
    "nivel_verbo": 4,
    "justificacion_pedagogica": "Texto de justificación preservado"
  }
]
`;
} else if (estructuraMEI === 'MEI-Actualizado') {
  // --- LÓGICA MEI-ACTUALIZADO ---
  // (Este es el código exacto que tú proporcionaste, ahora funcionará)
  finalInstructions = `
### INSTRUCCIONES DE CORRECCIÓN:
Revisa cada objeto en el "BORRADOR DE INDICADORES".

1.  **Validar Finalidad (REGLA DE ORO):** Revisa el "id_texto". Si encuentras CUALQUIER palabra de finalidad (como "para"), **reescribe** esa frase para eliminarla.
2.  **Validar Sintaxis:** Asegúrate de que "id_texto" comience con el verbo en **TERCERA PERSONA PRESENTE INDICATIVO PLURAL**.

3.  **Preservar Datos (MANDATORIO):** Debes mantener intactas las siguientes claves del borrador original en tu JSON de salida:
    * **id_texto** (El texto original o corregido)
    * **verbo_utilizado**
    * **nivel_verbo**
    * **justificacion_pedagogica**

4.  **Separar Componentes (MANDATORIO):** Crea las claves **Habilidad**, **Contenido** y **Condición/Contexto** descomponiendo el "id_texto" (ya sea el original o el que corregiste).
5.  **Formato de Salida:** Devuelve ÚNICAMENTE el array JSON corregido.

### EJEMPLO DE OBJETO JSON DE SALIDA:
[
  {
    "id_texto": "CLASIFICAN las principales teorías del comercio...",
    "verbo_utilizado": "CLASIFICAR",
    "nivel_verbo": 2,
    "justificacion_pedagogica": "Este indicador mide el peldaño...",
    "Habilidad": "CLASIFICAN",
    "Contenido": "Las principales teorías del comercio internacional (clásicas, neoclásicas y modernas).",
    "Condición/Contexto": "Incluyendo la explicación de sus postulados centrales..."
  }
]
`;
}

// 4. ENSAMBLAR Y DEVOLVER
const promptFinal = `
${promptBase}

### BORRADOR DE INDICADORES (A CORREGIR):
${JSON.stringify(borrador_indicadores, null, 2)}

${finalInstructions}

### TU TAREA:
Ejecuta la corrección. Devuelve solo el array JSON final.
`;

// 5. DEVOLVER PROMPT Y PRESERVAR DATOS
// Devolvemos solo el prompt. El siguiente nodo (Validador)
// también leerá el contexto desde 'Procesar Uno por Uno'.
return [{ 
  json: { 
    prompt: promptFinal
    // Ya no es necesario pasar el contexto aquí
  } 
}];

// --- Fin: Código CORREGIDO para "Constructor de Prompt (Agente 3 - Supervisor)" ---

// --- Inicio: Código para "Validar Salida Agente 1" (ID b9f3...) ---

// 1. OBTENER DATOS DE ENTRADA
// 'inputData' tiene TODO: prompt, objetivo_actual, contexto_superior, Verbos, nivel_ra,
// Y el 'output' de la IA.
const inputData = $input.first().json;

// Leemos la respuesta de la IA desde el campo 'output' (como en tu debug)
const rawResponse = inputData.output;

// 2. FUNCIÓN DE EXTRACCIÓN (Simple)
// Busca el primer '{' y el último '}'
function extractJson(text) {
  const jsonStart = text.indexOf('{');
  const jsonEnd = text.lastIndexOf('}');
  const jsonString = text.substring(jsonStart, jsonEnd + 1);
  return JSON.parse(jsonString);
}

// 3. PARSEAR LA RESPUESTA
const parsedOutput = extractJson(rawResponse);

// 4. PREPARAR EL ITEM DE SALIDA (LA CLAVE)
// Empezamos creando una copia de TODOS los datos que entraron.
// Esto preserva: objetivo_actual, contexto_superior, estructuraMEI, Verbos, nivel_ra, etc.
const outputItem = { ...inputData };

// 5. Borramos los campos que ya no necesitamos
delete outputItem.prompt;
delete outputItem.output; // Borramos la respuesta 'output' cruda

// 6. Agregamos los nuevos campos parseados al objeto
outputItem.rol_profesional_simulado = parsedOutput.rol_profesional_simulado;
outputItem.escenario_desafio_narrativo = parsedOutput.escenario_desafio_narrativo;

// 7. DEVOLVER EL OBJETO COMPLETO Y FUSIONADO
// El siguiente nodo (Constructor 2) ahora SÍ recibirá todo.
return [outputItem];

// --- Fin: Código para "Validar Salida Agente 1" (ID b9f3...) ---

// --- Inicio: Código CORREGIDO para "Validar Salida Agente 2" ---
const itemData = $input.first().json;
const rawResponse = itemData.output;

// 1. FUNCIÓN DE EXTRACCIÓN ROBUSTA (Busca el bloque de código)
function extractJsonArray(text) {
  // 1. Buscamos el bloque de código JSON (ignora texto inicial y final)
  const codeBlockMatch = text.match(/```json\s*(\[[\s\S]*?\])\s*```/);
  let jsonString;

  if (codeBlockMatch && codeBlockMatch[1]) {
    jsonString = codeBlockMatch[1];
  } else {
    // 2. Si no hay bloque de código, busca el array directo (método antiguo)
    const arrayStart = text.indexOf('[');
    const arrayEnd = text.lastIndexOf(']');
    if (arrayStart === -1 || arrayEnd === -1) {
        throw new Error("No se pudo encontrar un array JSON válido en la respuesta del Agente 2.");
    }
    jsonString = text.substring(arrayStart, arrayEnd + 1);
  }

  return JSON.parse(jsonString);
}

// 2. PARSEAR EL BORRADOR (El array de ILs/IDs)
const borrador_indicadores = extractJsonArray(rawResponse);

// 3. PREPARAR EL ITEM DE SALIDA (FUSIÓN)
const outputItem = { ...itemData };

delete outputItem.prompt;
delete outputItem.output; 

// 4. AÑADIR EL BORRADOR
outputItem.borrador_indicadores = borrador_indicadores;

return [outputItem];
// --- Fin: Código CORREGIDO para "Validar Salida Agente 2" ---

// --- Inicio del Código para el Nodo "Constructor de Prompt (Agente 1)" ---

// 1. OBTENER DATOS
// Todos los datos necesarios (globales y del bucle) están en el ítem JSON de entrada ($json).
const itemData=$('Procesar Uno por Uno').first().json;

// Datos globales (del nodo 'Set Data (Global)')
const resultadosFormativos = itemData.contexto_superior.resultadosFormativos;
const nombre_curso = $('Webhook').first().json.body.nombre_curso;
const trimestre = $('Webhook').first().json.body.trimestre;

// Dato del bucle (del nodo 'Procesar Uno por Uno (RA)')
const objetivo_actual = itemData.objetivo_actual; // Este es el RA (o AE)

// 2. PREPARAR PROMPT BASE (SYSTEM PROMPT)
// Esta es la directiva para el Agente 1: "Constructor de Mundos"
const promptBase = `Eres un "Constructor de Mundos" (World-Builder) experto en diseño instruccional y storytelling. Tu única tarea es establecer un contexto narrativo para una misión de aprendizaje.

### REGLAS DE MISIÓN:
1.  **Rol Profesional:** Asigna un rol profesional simulado (ej. "Ingeniero de Software Junior", "Consultor Pedagógico") que sea coherente con el curso y el trimestre.
2.  **Escenario Desafío:** Crea un escenario narrativo breve (la "misión") que el estudiante (en su rol) debe resolver. Esta misión debe estar basada en el Resultado de Aprendizaje (RA) y alineada con el Resultado Formativo (RF).
3.  **Modular por Trimestre:** Si el trimestre es bajo (1-3), el rol y la misión deben ser de "asistente" o "en formación". Si es alto (8-10), debe ser de "líder" o "consultor".
4.  **PROHIBICIÓN DE FINALIDAD:** El escenario *jamás* debe incluir la finalidad (ej. "para lograr X", "con el fin de Y"). La finalidad está implícita en la misión.

### FORMATO DE SALIDA OBLIGATORIO:
Tu respuesta DEBE SER ÚNICAMENTE un objeto JSON con dos claves: "rol_profesional_simulado" y "escenario_desafio_narrativo".

Ejemplo de salida:
{
  "rol_profesional_simulado": "Diseñador/a Pedagógico/a en Formación",
  "escenario_desafio_narrativo": "Te has integrado al Centro de Innovación Pedagógica. Tu supervisor te encarga la tarea crítica de construir un informe que conecte las teorías del aprendizaje fundamentales con los principios del diseño inclusivo."
}
`;

// 3. PREPARAR TAREA (USER PROMPT CON DATOS)
// Inyectamos los datos necesarios para que el Agente 1 construya el contexto
const rfTexto = (resultadosFormativos && resultadosFormativos.length > 0) ? resultadosFormativos[0].texto : "No disponible";
const raTexto = objetivo_actual.texto;
const cursoTexto = nombre_curso || "Nombre de curso no disponible";
const trimestreNum = trimestre || 4; // Usamos un default (trimestre 4) si no está disponible

const tareaTexto = `### DATOS DE ENTRADA:

- **Resultado Formativo (Visión General):**
  "${rfTexto}"

- **Resultado de Aprendizaje (Misión Específica):**
  "${raTexto}"

- **Nombre del Curso (Contexto/Bioma):**
  "${cursoTexto}"

- **Trimestre (Nivel de Seniority):**
  ${trimestreNum}

### TU TAREA:
Genera el objeto JSON de salida basado en estos datos.
`;

// 4. ENSAMBLAR Y DEVOLVER
const promptFinal = `${promptBase}\n\n${tareaTexto}`;

// Devolvemos el prompt Y pasamos toda la data (itemData)
// para que los siguientes nodos puedan usarla.
return [{ 
  json: { 
    prompt: promptFinal, 
    ...itemData // Pasa todos los datos: objetivo_actual, resultadosFormativos, etc.
  } 
}];
