// Este código valida la estructura del JSON y limpia su contenido de Markdown.

const llmOutput = $input.first().json.output;

// 1. Limpia cualquier posible marcador de código del LLM.
const cleanedText = llmOutput.replace(/^```json\s*/, '').replace(/```\s*$/, '');

// 2. Parsea el string a un objeto JSON.
const parsedJson = JSON.parse(cleanedText);

// 3. Define las 5 claves que DEBEN existir.
const requiredKeys = [
  'evaluacionPorEje',
  'analisisDebilidades',
  'analisisFortalezas',
  'sugerenciasMejora',
  'comentariosProceso'
];
const actualKeys = Object.keys(parsedJson);

// 4. Valida que el número de claves sea exactamente 5.
if (actualKeys.length !== requiredKeys.length) {
  throw new Error(`Validación Fallida: El JSON tiene ${actualKeys.length} claves en lugar de las 5 esperadas.`);
}

// 5. Valida que todas las claves requeridas estén presentes.
const missingKeys = requiredKeys.filter(key => !actualKeys.includes(key));
if (missingKeys.length > 0) {
  throw new Error(`Validación Fallida: Faltan las siguientes claves en el JSON: ${missingKeys.join(', ')}`);
}

// 6. NUEVO: Limpieza de Contenido (Markdown)
// Itera sobre cada una de las claves requeridas para limpiar su valor.
for (const key of requiredKeys) {
  let value = parsedJson[key];
  if (typeof value === 'string') {
    // Elimina los asteriscos de las listas (ej. "* texto" -> "texto")
    // La expresión regular busca al inicio de cada línea (flag 'm') un posible asterisco y espacios.
    value = value.replace(/^\s*\*\s+/gm, '');

    // Elimina los dobles asteriscos de la negrita (ej. "**texto**" -> "texto")
    // La expresión regular busca texto envuelto en dobles asteriscos.
    value = value.replace(/\*\*(.*?)\*\*/g, '$1');

    // Asigna el valor limpio de vuelta al objeto.
    parsedJson[key] = value.trim();
  }
}

// 7. Si todas las validaciones y la limpieza pasan, devuelve el objeto listo.
return {
  json: {
    evaluacion_di: parsedJson
  }
};
